sap.ui.define(["sap/ui/core/mvc/Controller",
       	"sap/ui/model/json/JSONModel"], function (Controller, JSONModel) {
	"use strict";
	return Controller.extend("agl.test.app.mytestapp.controller.Home", {
		onInit: function () {
				var oJSONModel = new JSONModel({});
			this.getOwnerComponent().setModel(oJSONModel, "passingParameters");
			if(1===1){ 

				var A = 12;
				var b = 13;
			}
			
		},
// my second commit

		/**
		 *@memberOf agl.test.app.mytestapp.controller.Home
		 */
		onValueHelp: function (oEvent) {
			//This code was generated by the layout editor.
						var oView = this.getView();
			var oDialog = oView.byId("dialogPlantLookup");
			if (!oDialog) {
				oDialog = sap.ui.xmlfragment(oView.getId(), "agl.test.app.mytestapp.view.valuehelp", this);
				this.getView().addDependent(oDialog);
				oDialog.open();
			}
			else {
				oDialog.open();
			}

			
		},
				onPlantCancel: function () {
			var oView = this.getView();
			var oDialog = oView.byId("dialogPlantLookup");
			if (oDialog) {
				oDialog.destroy();
			}
		},
		onPlantSelect: function () {
			var oView = this.getView();
			var oDialog = oView.byId("dialogPlantLookup");
			var sPlant = this.getOwnerComponent().getModel("passingParameters").getProperty("/selectedPlant");
			//	jQuery.sap.require("jquery.sap.storage");
			this.byId("input0").setValue(sPlant);
			if (oDialog) {
				oDialog.destroy();
			}
		}
	});
});